import React, { useState } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import { useForm } from 'react-hook-form';
import { init, sendForm } from 'emailjs-com';
init('user_6PEnWgQ1LFiBmJrspbPFv');



const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: '#02012e',
        paddingBottom: "5vh",
    },
    contact: {
        fontSize: "3vw",
    },
    input: {
        width: "60vw",
    },
    message: {
        width: "60vw",
        height: "10em",
    },
    characters: {
        fontSize: "1.5vw",
    },
})
)

const Contact = () => {
    const [contactNumber, setContactNumber] = useState("000000");

    const generateContactNumber = () => {
      const numStr = "000000" + (Math.random() * 1000000 | 0);
      setContactNumber(numStr.substring(numStr.length - 6));
    }
  }

export default function Email() {
    const classes = useStyles();

    const { register, handleSubmit, watch, formState: { errors }} = useForm();

    const Contact = () => {
        const [contactNumber, setContactNumber] = useState("000000");

        const generateContactNumber = () => {
          const numStr = "000000" + (Math.random() * 1000000 | 0);
          setContactNumber(numStr.substring(numStr.length - 6));
        }
      }

    const onSubmit = data => 
        console.log(data);
        generateContactNumber();
        sendForm('default_service', 'user_6PEnWgQ1LFiBmJrspbPFv', '#contact-form')
            .then(function(response) {
            console.log('SUCCESS!', response.status, response.text);
            }, function(error) {
            console.log('FAILED...', error);
            });

    const message = watch('message') || "";
    const messageCharsLeft = 1500 - message.length;


  return (
    <div className={classes.contact}>
        <h1>Let's connect</h1>
        <form id='contact-form' onSubmit={handleSubmit(onSubmit)}>
            {errors.user_name && errors.user_name.type === "required" && (
            <div role="alert">Name is required<br/></div>
            )}
            <input 
                className={classes.input} 
                type='text' 
                name='user_name' 
                maxLength='30'
                aria-invalid={errors.user_name ? "true" : "false"}
                {...register("user_name", {
                    required: "Required",
                  })}
                placeholder='Name' />
            <br/>
            {errors.user_email && errors.user_email.type === "required" && (
            <div role="alert">Email is required<br/></div>
            )}
            <input 
                className={classes.input} 
                type='email' 
                name='user_email' 
                maxLength='40'
                aria-invalid={errors.user_name ? "true" : "false"}
                {...register("user_email", {
                    required: "Required",
                  })}
                placeholder='Email' />
            <br/>
            {errors.message && errors.message.type === "required" && (
            <div role="alert">Message is required<br/></div>
            )}
            <textarea 
                className={classes.message} 
                type='message' 
                maxLength='1500'
                {...register("message", {
                    required: "Required",
                  })}
                placeholder='Message'/>
            <br/>
            <p className={classes.characters}>Characters remaining: {messageCharsLeft}</p>
            <input type='submit' value='Send' />
            <input type='hidden' name='contact_number' value={contactNumber} />
        </form>
    </div>
  );
}
