import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Footer from "../Footer";
import Template from "./Template";
import Grid from "@material-ui/core/Grid";
import Carousel from "react-material-ui-carousel";

const useStyles = makeStyles((theme) => ({
    root: {
        height: "100vh",
        width: "100%",
        overflow: "auto",
    },
    extra: {
        backgroundColor: "white",
        paddingRight: "6vw",
        paddingLeft: "6vw",
        padding: "3vw",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
    },
    description: {
        fontSize: "min(8vw, 20px)",
        paddingRight: "2vw",
        color: "#0a0a0a",
        fontWeight: "400",
        paddingBottom: "0vh",
        width: "100%",
    },
    pic1: {
        width: "100%",
        maxWidth: "700px",
        height: "auto",
        position: "relative",
        display: "block",
        padding: "2vh",
        paddingTop: "0vh",
        paddingBottom: "0vh",
        margin: "0",
    },
    pic2: {
        width: "100%",
        maxWidth: "700px",
        height: "auto",
        position: "relative",
        display: "block",
        margin: "0",
    },
    carousel: {
        width: "80vw",
        maxWidth: "700px",
        height: "60vw",
        maxHeight: "500px",
    },
}));
export default function Toothbrush() {
    const classes = useStyles();
    const project = "toothbrush";
    const techList = ["SolidWorks", "Arduino", "FDM Printing", "Systems Integration"];
    return (
        <div className={classes.root}>
            <Template
                title="Toothbrush Tester"
                project={project}
                tagline="Lifetime Simulation Device"
                description="In collaboration with the Tufts Dental School, I worked with a team of four engineers to design and build a lifetime
                simulation device capable of stress testing up to 10 toothbrushes over thousands of cycles. After lots of iterating and testing, we
                delivered a fully functional prototype and were awarded the James O'Leary award for most outstanding capstone design out of 50 other students."
                timeframe="4 months"
                github={false}
                techList={techList}
            ></Template>
            <div className={classes.extra}>
                <img
                    src={`static/projects/${project}/TBT_closeup.png`}
                    alt="project"
                    className={classes.pic1}
                />
            </div>
            <div className={classes.extra}>
                <p className={classes.description}>
                    As design lead, I generated the majority of the physical design concepts,
                    produced and maintained a 350+ part SolidWorks assembly, tested the prototype
                    designs, and drove feedback-driven iteration. I created technical drawings with
                    GD&T and communicated machining instructions with the machine shop. I also
                    collaborated with our electrical engineer to fabricate a custom PCB which
                    facilitated the connections between the Arduino, power supplies, motor drivers,
                    DC pump, limit switches, and LCD screen.
                </p>
            </div>
            <div className={classes.extra}>
                <Carousel navButtonsAlwaysVisible={true}>
                    <div className={classes.carousel}>
                        <img
                            src={`static/projects/${project}/TBT_subassembly.png`}
                            alt="project"
                            className={classes.pic2}
                        />
                    </div>

                    <div className={classes.carousel}>
                        <img
                            src={`static/projects/${project}/TBT_side.png`}
                            alt="project"
                            className={classes.pic2}
                        />
                    </div>
                </Carousel>
            </div>
            <div className={classes.extra}>
                <iframe
                    width="800px"
                    height="451px"
                    src="https://www.youtube.com/embed/Z0ocjP-AUJQ"
                    title="YouTube video player"
                    frameborder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowfullscreen
                ></iframe>
            </div>
            <Footer />
        </div>
    );
}
