import React from 'react';
import emailjs from 'emailjs-com';
import swal from 'sweetalert';
import { makeStyles } from "@material-ui/core/styles";
import { useForm } from 'react-hook-form';

const useStyles = makeStyles((theme) => ({
    title: {
        fontSize: "4vw",
        color: "#02012e",
    },
    input: {
        width: "60vw",
    },
    message: {
        width: "60vw",
        height: "10em",
    },
    characters: {
        fontSize: "1.5vw",
    },
})
)

export default function Email() {
    const classes = useStyles();

    const { register, handleSubmit, watch, errors } = useForm();

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('outlook', 'basic', e.target, 'user_6PEnWgQ1LFiBmJrspbPFv')
      .then((result) => {
          console.log(result.text);
          swal.fire({
            title: 'Email Successfully Sent',
            icon: 'success'
          })
      }, (error) => {
          console.log(error.text);
          swal.fire({
            title: 'Email Failed to Send',
            icon: 'error'
          })
      });
  }

  return (
      <div className={classes.title}>
          Let's Connect
         <form className={classes.form} onSubmit={sendEmail}>
             {errors.user_name && errors.user_name.type === "required" && (
             <div role="alert">Name is required<br/></div>
             )}
             <input 
                className={classes.input} 
                type="text" 
                name="user_name" 
                aria-invalid={errors.contact_number ? "true" : "false"}
                {...register("user_name", {
                    required: "Required",
                  })}
                placeholder='Name' />
            <br/>

            <input className={classes.input} type="text" name="user_name" />
            <label>Email</label>
            <input type="email" name="user_email" />
            <label>Message</label>
            <textarea className={classes.message} name="message" />
            <input type="submit" value="Send" />
          </form>
      </div>

  );
}
