import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Footer from "../Footer";
import Template from "./Template";

const useStyles = makeStyles((theme) => ({
    root: {
        height: "100vh",
        width: "100%",
        overflow: "auto",
    },
    extra: {
        backgroundColor: "white",
        padding: "2vw",
    },
}));
export default function Toothbrush() {
    const classes = useStyles();
    const techList = ["SolidWorks", "Arduino", "FDM Printing", "Systems Integration"];
    return (
        <div className={classes.root}>
            <Template
                title="Toothbrush Tester"
                project="toothbrush"
                tagline="Lifetime Simulation Device"
                description="In collaboration with the Tufts Dental School, I worked with a team of four engineers to design and build a lifetime
                simulation device capable of stress testing up to 10 toothbrushes over thousands of cycles. After lots of iterating and testing, we
                delivered a fully functional prototype and were awarded the James O'Leary award for most outstanding capstone design out of 50 other students."
                timeframe="4 months"
                github={false}
                techList={techList}
            ></Template>
            <div className={classes.extra}>
                <p>
                    As design lead, I was responsible for brainstorming, sketching, modeling,
                    rendering, fabricating, and assembling all of the device's components. My team
                    generated technical requirements and aided in the sketching and brainstorming
                    process. I generated the majority of the physical design concepts, produced and
                    maintained a 350+ part SolidWorks assembly, tested the prototype designs, and
                    drove feedback-driven iteration. I created technical drawings with GD&T and
                    communicated machining instructions with the machine shop. I also collaborated
                    with our electrical engineer to fabricate a custom PCB which facilitated the
                    connections between the Arduino, power supplies, motor drivers, DC pump, limit
                    switches, and LCD screen.
                </p>
            </div>
            <Footer />
        </div>
    );
}
