import React from 'react';
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: '#02012e',
        paddingBottom: "5vh",
    },
    input: {
        width: "60vw",
    },
    message: {
        width: "60vw",
        height: "10em",
    },
})
)


export default function Email() {
    const classes = useStyles();
  return (
    <div className={classes.contact}>
        <h1>Contact</h1>
        <form id='contact-form'>
        <input className={classes.input} type='text' name='user_name' placeholder='Name' />
        <br/>
        <input className={classes.input} type='email' name='user_email' placeholder='Email' />
        <br/>
        <textarea className={classes.message} type='message' placeholder='Message'/>
        <br/>
        <input type='submit' value='Send' />
        </form>
    </div>
  );
}
